              const auto& set2 = firstChar2Suffix[keys[j]];
              std::vector<std::string_view> unionSet;
              std::set_union(set1.begin(), set1.end(), set2.begin(), set2.end(),
                  std::back_inserter(unionSet));
              res += (set1.size() - unionSet.size()) * (set2.size() -
  unionSet.size()) * 2;
          }
      }
      return res;
  }
