class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
		// char -> {string1[1:], string2[1:], ...}
        unordered_map<char, unordered_set<string>> hashMap;
        for (auto& idea : ideas) {
            hashMap[idea[0]].insert(idea.substr(1));
        }
        long long res = 0;
		// iterate all char pairs
		// i.e., a->b, a->c, ... y->z
        for (int i = 0; i < 26; ++i) {
            for (int j = i + 1; j < 26; ++j) {
                char a = i + 'a';
                char b = j + 'a';
                auto& setA = hashMap[a];
                auto& setB = hashMap[b];
				// count the intersections between two groups
                int intersect = 0;
                for (auto& s : setA) {
                    if (setB.find(s) != setB.end()) intersect++;
                }
				// to get the # of string[1:] in both groups without any intersections
                int setADiff = setA.size() - intersect;
                int setBDiff = setB.size() - intersect;
				// *2: a->b & b->a
                res += (long long)setADiff * setBDiff * 2;
            }
        }
        return res;
    }
};
```

