class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
        unordered_map<string, bool> m;
        set<char> charSet;

        for (string& idea : ideas) {
            m[idea] = true;
            charSet.insert(idea[0]);
        }

        vector<vector<long long>> dp(26, vector<long long>(26,0));

        for (string& idea : ideas) {
            char startingChar = idea[0];
            for (char targetChar : charSet) {
                idea[0] = targetChar;
                if (m.count(idea) == 0) {
                    dp[startingChar-'a'][targetChar-'a']++; 
                }
            }
        }

        long long res = 0;

        for (char startingChar : charSet) {
            for (char targetChar : charSet) {
                if (startingChar != targetChar) {
                    res += dp[startingChar-'a'][targetChar-'a'] * dp[targetChar-'a'][startingChar-'a'];
                }
            }
        }

        return res;
    }
};
