# Code Using Map (87/89 Test Case passed TLE)
```
class Solution {
public:
    long long distinctNames(vector<string>&ideas) 
    {
        unordered_map<string,bool>exists;
        vector<vector<int>>indexesatchar(26,vector<int>(0));
        for(int i=0;i<ideas.size();i++)
        {
            exists[ideas[i]]=true;
            indexesatchar[ideas[i][0]-'a'].push_back(i);
        }
        vector<vector<int>>replace(26,vector<int>(0));
        for(char ch='a';ch<='z';ch++)
        {
            for(char x='a';x<='z';x++)
            {
                int notexist=0;
                for(auto j:indexesatchar[ch-'a'])
                {
                    ideas[j][0]=x;
                    if(!exists[ideas[j]])
                    {
                        notexist++;
                    }
                }
                replace[ch-'a'].push_back(notexist);
            }
        }
        long long ans=0;
        for(char ch='a';ch<='z';ch++)
        {
            for(char x=ch;x<='z';x++)
            {
                ans+=replace[ch-'a'][x-'a']*replace[x-'a'][ch-'a'];
            }
        }
        ans*=2;
        return ans;
    }
};
```

