class Solution {
public:
    struct TrieNode
    {
        TrieNode* child[26];//node pointing to other 26 possible nodes...if they dont exist then null
        bool flag=false;//for knowing if the node is the end of a possible word or not
        bool Inserted=false;//to avoid duplicates in ans...if the word already inserted
        string str;//keeping record of string that has made its way to here
        bool containChar(char character)
        {
            return (child[character-'a']!=NULL);//to see if the node contains the character as next node
        }
        void putNode(char nxtchar,TrieNode* node)
        {
            child[nxtchar-'a']=node;//if next character doesnt exist in trie the to insert
        }
        TrieNode* getNode(char character)
        {
            return child[character-'a'];//maybe traverse the trie further if we get our way in dfs
        }
        void setEnd()
        {
            flag=true;//sets end tag to node denoting it being end of one word
        }
        bool isEnd()
        {
            return flag;//to know if its the end or not
        }
        bool didInsert()
        {
            return Inserted;//self explanatory
        }
        void setdidInsert()
        {
            Inserted=true;//      "
        }
