        for(i=0;i<word.size();i++)
        {
            if(node->containChar(word[i])==false)
            {
                node->putNode(word[i],new TrieNode());//if not present then insert
            }
            node=node->getNode(word[i]);//traverse further in trie (now that the character is inserted if it didnt exist before)
        }
        node->setEnd();//set end to final node after traversal of word
        node->str=word;//assign str
    }
    bool stringpresence(string &word)//to find string presence (similiar to insert word, just here we are checking the presence and incrementing the string pointer)
    {
        TrieNode* node=root;
        for(int i=0;i<word.size();i++)
        {
