        }
        vector<vector<int>>replace(26,vector<int>(0));
        //replace all the first characters of indexesatchar of the sets of strings starting with 'a'/'b'/'c'....'z' with other alphabets in 'a' to 'z' and check their existence and incrementing count if doesn't existand build adjacency matrix
        for(char ch='a';ch<='z';ch++)
