                    if(!stringpresence(ideas[j]))
                    {
                        notexist++;
                    }
                }
                replace[ch-'a'].push_back(notexist);
            }
        }
        //now we have adjacency matrix (vector<vector<int>>) where the ith row denotes that "if we replace the (char)(i+97) of the strings having it as first character with the character (char)(j+97) (denoted by the jth column) then how many strings won't exist in "ideas"(the guven vector of strings)....which means that we can choose them with the set of strings which don't exist when their first character of (char)(j+97) is replaced by (char)(i+97) (denoted by count at row j and column i)...so we multiply them and add in our final ans.
        long long ans=0;
        for(char ch='a';ch<='z';ch++)
        {
            for(char x=ch;x<='z';x++)
            {
                ans+=replace[ch-'a'][x-'a']*replace[x-'a'][ch-'a'];
            }
        }
        ans*=2;//the valid pairs are also valid when words are interchanged
        return ans;
    }
};
