class Solution {
public:
//app 1: gives tle
    long long distinctNames(vector<string>& s) {
        int n=s.size();
        unordered_set<string> st;

        for(string &w: s) st.insert(w);
        long long ans=0;

        for(int i=0; i<n; i++){
            for(int j=i+1; j<n; j++){
                string w1=s[i];
                string w2=s[j];
                //cout<<j<<" "<<w2<<endl;
                //check condtn
                if(w1[0] != w2[0]){
                    swap(w1[0],w2[0]);
                    //cout<<w1<<" "<<w2<<endl;
                    if(st.find(w1) == st.end() &&
                       st.find(w2) == st.end()){
                           ans += 2;
                       }
                }
            }
        }

        return ans;
    }
};
```
# Code approach 2 [Accepted]
```
class Solution {
public:
//app 2: O(n*m)
    long long distinctNames(vector<string>& s) {
        int n=s.size();

        //make an array of set
        unordered_set<string> st[26];

       //group words by initials [a to z]
        for(string &w: s){
            st[w[0] - 'a'].insert(w.substr(1));
        }

        long long ans=0;
        for(int i=0; i<26; i++){
            for(int j=i+1; j<26; j++){
                int mutual=0;
                //find mutual words 
                for(auto &word: st[i]){
                     if(st[j].count(word)) mutual++;
                }

// Valid names are only from distinct suffixes in both groups.
 // Since we can swap a with b and swap b with a to create two valid names, multiple answer by 2.
                long long combn = (st[i].size()-mutual)*(st[j].size()-mutual);
                ans += 2*combn;
            }
        }

        return ans;
    }
};
```
