class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
        unordered_map<char , unordered_set<string>>mp;
        for(auto &s : ideas)
            mp[s[0]].insert(s.substr(1 , s.size() - 1));
        long long ans = 0 ;
        for(auto&[key , st]:mp)
