class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
        long long result = 0;
        // Group by first letter and it's suffix
        vector<unordered_set<string>> count(26);

        for (auto& idea : ideas) {
            count[idea[0] - 'a'].insert(idea.substr(1));
        }

        // permutations = unique items in set1 * unique items in set2 * 2
        for (int i = 0; i < 26; i++) {
            for (int j = i + 1; j < 26; j++) {
                long unique1 = 0, unique2 = 0;

                for (auto& suffix : count[i]) {
                    if (count[j].find(suffix) == count[j].end())
                        unique1++;
                }

                for (auto& suffix : count[j]) {
                    if (count[i].find(suffix) == count[i].end())
                        unique2++;
                }

                result += unique1 * unique2 * 2;
            }
        }

        return result;
    }
};
