class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
        map<char,unordered_set<string>> m;
        long long ans=0;
        for(auto it:ideas){
            m[it[0]].insert(it.substr(1,it.size()-1));
        }
        for(auto it=m.begin();it!=m.end();it++){
            for(auto it2=next(it,1);it2!=m.end();it2++){
                unordered_set<string> s;
                s.insert((it->second).begin(),(it->second).end());
                s.insert((it2->second).begin(),(it2->second).end());
                ans+=(s.size()-(it->second).size())*(s.size()-(it2->second).size());
            }
        }

        return ans*2;
    }
};
```

