class Solution {
public:
    long long distinctNames(vector<string>& ideas) {
        unordered_map<string,int> mtail;
        vector<vector<string>> mhead(26);
        for(auto &s:ideas){
            int index = s[0]-'a';
            string st = s.substr(1);
            mhead[index].push_back(st);
            if(mtail.count(st)) mtail[st] |= (1<<index);
            else mtail.insert({st,1<<index});
        }
        long long ans = 0;
        for(int i = 0; i < 26; i++){
            int dup[26]{};
            for(auto &s:mhead[i]){
                int temp = mtail[s];
                for(int j = i+1; j<26;j++){
                    if(temp&(1<<j)) dup[j]++;
                }
            }
            int nsize = mhead[i].size();
            for(int j = i+1; j < 26;j++){
                int ndup = dup[j];
                int val = (nsize - ndup)*(mhead[j].size()-ndup);
                ans += (nsize - ndup)*(mhead[j].size()-ndup);
            }
        }
        return ans<<1;

    }
};
