            arr[ideas[i].charAt(0) - 'a'].add(ideas[i].substring(1));
        }
        for(int i = 0; i < 25; i++){
            for(int j = i + 1; j < 26; j++){
                Set<String> set = new HashSet<>();
                set.addAll(arr[i]);
                set.addAll(arr[j]);
                result += (arr[i].size() - set.size()) * (arr[j].size() - set.size());
            }
        }
        return result * 2;
    }
}

