def distinct_names(ideas)
  h = Hash.new(0)
  ideas.each { |idea| h[idea[1..]] |= 1 << idea[0].ord - ?a.ord }
  heads = Array.new(26) { [0] * 26 }
  2 * ideas.sum { |idea|
    head = idea[0].ord - ?a.ord
    existing = h[idea[1..]]
    26.times.sum {
      if existing[_1] > 0
        0
      else
        heads[head][_1] += 1
        heads[_1][head]
      end
    }
  }
end
