/**
 * @param {string[]} ideas
 * @return {number}
 */
var distinctNames = function (ideas) {
  let map = {};
  // storing data as key value pair
  // key is char & value is suffix that is without 1st letter
  for (const idea of ideas) {
    let char = idea[0];
    if (!map[char]) map[char] = new Set();
    map[char].add(idea.slice(1));
  }
  let result = 0;
  // looping over map  just like nested for loop every 1 char with rest all char

  for (let char1 in map) {
    for (let char2 in map) {
      // if char are same means we cannot add them to result
      if (char1 === char2) {
        continue;
      }
      // Here we have to find different suffix
      // because if suffix are same we have a same word in gives ideas & hence we cannot increment count
      // idea is to get intersect that is get common suffix count & sub it from set of all suffix
      //
      let intersect = 0;
      for (let w of map[char1]) {
        if (map[char2].has(w)) {
          intersect++;
        }
      }
      let d1 = map[char1].size - intersect;
      let d2 = map[char2].size - intersect;
      result = result + d1 * d2;
    }
  }
  return result;
};

