class Solution:
    def distinctNames(self, ideas: List[str]) -> int:
        d={}
        #creating a dictionary with first letter as key
        for i in ideas:
            if ord(i[0]) in d:
                d[ord(i[0])].add(i[1:])
            else:
                d[ord(i[0])]={i[1:]}
        r=0  
        #comparing every set with each other      
        for i in range(97,123):
            if i not in d:
                continue
            for j in range(i+1,123):
                if j not in d:
                    continue
                # we dont need common element because that will lead to invalid sequence
                a=len(d[i].intersection(d[j]))
                #removing common element and add total combinations it to result 
                r+=((len(d[i])-a)*(len(d[j])-a))*2           
        return r        
