use std::collections::HashSet;

impl Solution {
    pub fn distinct_names(ideas: Vec<String>) -> i64 {
        let mut groups: Vec<HashSet<String>> = vec![HashSet::new(); 26];
        let mut pairs: i64 = 0;

        ideas.into_iter().for_each(|idea| {
            groups[(idea.as_bytes()[0] - b'a') as usize].insert(idea[1..].to_string());
        });

        for i in 0..25 {
            for j in (i + 1)..26 {
                let dup = groups[i].iter().filter(|&s| groups[j].contains(s)).count();
                pairs += 2 * ((groups[i].len() - dup) * (groups[j].len() - dup)) as i64;
            }
        }
        pairs
    }
}
```

### With HashMap

```
use std::collections::{HashMap, HashSet};

impl Solution {
    pub fn distinct_names(ideas: Vec<String>) -> i64 {
        let mut groups: HashMap<&str, HashSet<&str>> = HashMap::new();

        ideas.iter().for_each(|idea| {
            groups.entry(&idea[..1])
                .or_insert(HashSet::new()).insert(&idea[1..]);
        });

        let mut keys: Vec<&str> = groups.keys().map(|&key| key).collect();
        let mut pairs: i64 = 0;

        for &k1 in keys.iter() {
            groups.iter().for_each(|(&k2, v2)| {
                let mut dup = 0;
                for sfx in groups[k1].iter() {
                    if v2.contains(sfx) {
                        dup += 1;
                    }
                }
                pairs += 2 * (groups[k1].len() as i64 - dup) * (v2.len() as i64 - dup);
            });
            groups.remove(k1);
        }
        pairs
    }
}
