class Solution {
    public long distinctNames(String[] ideas) {
        int[] firstLetters = new int[26];
        Set<String> wordSet = new HashSet<>();
        for (var idea : ideas) {
            wordSet.add(idea);
            firstLetters[idea.charAt(0) - 'a']++;
        }

        long[][] replace = new long[26][26];

        for (var word : wordSet) {
            int c = word.charAt(0) - 'a';
            for (int i = 0; i < 26; ++i) {
                if (c != i && firstLetters[i] > 0) {
                    String newWord = (char)(i + 'a') + word.substring(1);
                    if (!wordSet.contains(newWord)) {
                        // from char c to char i
                        replace[c][i]++;
                    }
                }
            }
        }

        long sum = 0;
        for (int i = 0; i < 26; ++i) {
