public class Solution {
    public long DistinctNames(string[] ideas) {
        HashSet<string>[] arr = new HashSet<string>[26];
        for(int i = 0;i < ideas.Length;i++){
            int idx = ideas[i][0] - 'a';
            if(arr[idx] == null) arr[idx] = new HashSet<string>();
            arr[idx].Add(ideas[i].Substring(1));
        }
        long res = 0;
        for(int i = 0;i < 25;i++){
            if(arr[i] == null) continue;
            for(int j = i + 1;j < 26;j++){
                // find intersect between two hashset
                // intersect between two sets is part of smallest one
                if(arr[j] == null) continue;
                HashSet<string> smallest = arr[i], largest = arr[j];
                if(smallest.Count > largest.Count)
                    (smallest,largest) = (largest,smallest);
                int intersect = 0;
                foreach(var w in smallest)
                    if(largest.Contains(w)) intersect++;
                res+= 2 * (smallest.Count - intersect) * (largest.Count - intersect);
            }
        }
        return res;
    }
}
