long long distinctNames(vector<string>& ideas) {
       unordered_map<string,int> m;
       for(auto i:ideas){
           m[i]++;
       }
       long long ans =0;
       for(int i = 0;i<ideas.size();i++){
           for(int j = 0;j<ideas.size();j++){
               if(j == i){
                   continue;
               }
               string s1 = ideas[i];
               string s2 = ideas[j];
               swap(s1[0],s2[0]);
               if(m.find(s1) == m.end() && m.find(s2)==m.end()){
                   ans++;
               }
           }
       }
       return ans;
   }

