                char ch1=st2.charAt(0);
               st1= st1.replace(st1.charAt(0),ch);
               st2= st2.replace(st2.charAt(0),ch1);
               if(!hs.contains(st1)){
                   ans++;
               }
                if(!hs.contains(st2)){
                   ans++;
               }
            }
        }
   return ans; }
}
