class Solution {
public:
    int maximumXOR(vector<int>& nums) {
        int n = nums.size();
        int ans=0;
        // for every bit check the cnt of the times 1 appears in the array. If the cnt is great than 1, then we can consider that bit in our ans.
        // this is becuase the given op can unset any bit of the number in nums[i].
        //so if the cntOne is odd we dont have to change that bit in any of the nums[i] because the xor of odd number of one is 1.
        // but if the cntOne if even we must unset any one nums[i] bit in the array and consider that bit in our answer.
        // So it can be conculded that even if a single 1 occurs in that position in any nums[i], we can consider that bit in our ans.
        for(int i=31;i>=0;i--) {
            int cntOne = 0;
            for(int j=0;j<n;j++) {
                cntOne += ((nums[j]>>i)&1);
            }
            if(cntOne == 0)
                continue;
            ans += (1<<i);
        }
        return ans;
    }
};  
