class Solution:
    def maximumXOR(self, nums: List[int]) -> int:
        nums = list(map(lambda x: format(x, '032b'), nums))
        freqs = [0]*32
        for num in nums:
            for i, bit in enumerate(num):
                if bit == '1':
                    freqs[i] += 1

        s = ''
        for cnt in freqs:
            if cnt > 0:
                s += '1'
            else:
                s += '0'
        return int(s, 2)
