        void insert(int x){
            Node* temp=root;
            for(int i=30;i>=0;i--){
                if((x>>i)&1){
                   if(temp->right)temp=temp->right;
                   else{
                       temp->right=new Node();
                       temp=temp->right;
                   }
                }
                else{
                    if(temp->left)temp=temp->left;
                    else{
                        temp->left=new Node();
