class Solution {
public:
    int maximumXOR(vector<int>& nums) 
    {
        int n=nums.size(),i,ans=0;
        vector <int> cnt(30);
        for(i=0;i<30;i++)
        {
            for(auto x : nums)
            {
                //count number of element that has ith bit set
                cnt[i]+=((x>>i)&1);
            }
            //if there is atleast one element in nums which has ith bit set
            if(cnt[i]>0)
            {
                //set the ith bit in ans
                ans+=(1<<i);
            }
        }
        return ans;
    }
};

