class Solution {
public:
    int maximumXOR(vector<int>& nums) {
        vector<int>ans(32,0);
        for(int i:nums){
            for(int j=0;j<32;++j){
                if(i&(1<<j)){
                    ans[j]=1;
                }
            }
        }
        long long answer=0;
        for(int i=0;i<32;++i){
            if(ans[i])
            answer+=pow(2,i);
        }
        return answer;
    }
};
