class Solution {
public:
    int maximumXOR(vector<int>& nums) {
        vector<int> bit_cnt(32, 0);
        int pos;
        for(auto num : nums){
            pos = 0;
            while(num){
                bit_cnt[pos++] += num & 0x1;
                num >>= 1;
            }
        }
        for(int j = 0; j < nums.size(); ++j){
            pos = 0;
            while(nums[j]){
                if(nums[j] & 0x1){
                    if(!(bit_cnt[pos] & 0x1)){
                        bit_cnt[pos]--;
                    }
                }
                nums[j] >>= 1;
                ++pos;
            }
        }
        int ans = 0;
        for(int i = 0; i < 32; ++i){
            if(bit_cnt[i] & 0x1){
                ans += (1 << i);
            }
        }
        return ans;
    }
};
```

