class Solution {
public:
    int maximumXOR(vector<int>& a) 
    {
        int n = a.size();

        int bit[32];
        memset(&bit, 0x00, sizeof(bit));

        for(int i = 0; i < 32; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(a[j] & (1 << i))
                {
                    bit[i]++;
                }
            }
        }   


        int ans = 0;
        for(int i = 0; i < n; i++)
            ans ^= a[i];

        for(int i = 0; i < 32; i++)
        {
            if(!bit[i]) continue;
            ans |= (1 << i);
        }

        return ans;
    }
};
