class Solution {
public:

    // nums[i] AND (nums[i] XOR x) --> this means that you have the liberty to off some of the set bits of nums[i]
    // hence we need to find max xor of all elements with the power to set some ON bits to OFF
    // So what we could do is change every ON bit to OFF that is on the same position of another ON bit in the array
    // and keep only one ON bit at every position possible ( out of 32 indexes ) 

    // and that is the OR function
    int maximumXOR(vector<int>& nums) {
        int ans = 0;
        for(int i : nums)
            ans |= i;
        return ans;
    }
};
