```
impl Solution {
    pub fn count_prefixes(words: Vec<String>, s: String) -> i32 {
        words
            .iter()
            .filter(|w| w.len() <= s.len() && w.chars().zip(s.chars()).all(|(a, b)| a == b))
            .count() as _
    }
}
