class Solution {
public:
    int countPrefixes(vector<string>& words, string s) {

        unordered_map<string,int> map;
        for(auto it :words)
        {
            map[it]++;
        }
        int ans  = 0;
        unordered_set<string> seti(words.begin(), words.end());
        for(int j = 1;j <= s.size();j++)
        {
            string temp = s.substr(0,j);
            if(seti.find(temp) != seti.end())
            {
                seti.erase(temp);
                ans += map[temp]++;
                map.erase(temp);
            }
        }
        return ans;
    }
};
