class Solution {
public:
    bool digitCount(string nums) {

        map<int,int>mp;

        for(int i=0;i<nums.size();i++)
        {
            if(nums[i]!='0') //if any number is occuring zero times then do not add that number into map
                mp[i] = nums[i] - '0';
        }

        for(int i=0;i<nums.size();i++)
        {
            if(mp.find(nums[i]-'0') == mp.end())
                return false;

            mp[nums[i]-'0']--;
            if(mp[nums[i]-'0']==0) //if value become zero then remove it from the map.
                mp.erase(nums[i]-'0');
        }
        if(mp.size()==0) // if map is empty then return true.
            return true;
        else
            return false;

    }
};
```

