class Solution {
  public int[] successfulPairs(int[] spells, int[] potions, long success) {
    var n = spells.length;
    var m = potions.length;
    var pairs = new int[n];

    Arrays.sort(potions);

    for (var i=0; i<n; i++) {
      var minPotion = (int) Math.ceil((double) success / spells[i]);

      if (minPotion > potions[m-1]) continue;

      var l = 0;
