class Solution {
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        int n = spells.size(), m = potions.size();
        sort(potions.begin(), potions.end());
        vector<int> ans(n);
        for (int i{}; i<n; ++i){
            long long potion = ceil((double)success/spells[i]);
            int lower_bound_ind = lower_bound(potions.begin(), potions.end(), potion) - potions.begin();
            ans[i] = m-lower_bound_ind;
        }
        return ans;
    }
};
