class Solution {
public:

    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        int maxi = 0;
        unordered_map<int,int> dp;
        for(int i = 0 ; i<potions.size() ; i++){
            dp[potions[i]]++;
            maxi = max(potions[i],maxi);
        }

        vector<int> refer(maxi+1);

        refer[refer.size()-1] = dp[refer.size()-1];

        for(int i = refer.size()-2 ; i >= 0 ; i--){
            refer[i] = refer[i+1] + dp[i];
        }

        vector<int> result(spells.size());

        for(int i = 0 ; i < spells.size() ; i++){
            double t1 = success;
            double t2 = spells[i];
            long long temp = ceil(t1/t2);
            if(temp > maxi){
                result[i] = 0;
                continue;
            }
            result[i]= refer[temp];
        }
        return result;
    }
};
