class Solution {
private:
    int index=-1;
    void binSearch(vector<int>& potions,int low,int high,long long success,int pro){
        if(low>high){
            return;
        }
        int mid = low+(high-low)/2;
        if((long long)potions[mid]*(long long)pro>=success){
            index=mid;
            binSearch(potions,low,mid-1,success,pro);
        }
        else{
            binSearch(potions,mid+1,high,success,pro);
        }
    }
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        ios_base::sync_with_stdio(false);
        sort(potions.begin(),potions.end());
        for(int i=0;i<spells.size();i++){
            binSearch(potions,0,potions.size()-1,success,spells[i]);
            if(index==-1){
                spells[i]=0;
            }
            else{
                spells[i]=potions.size()-index;
            }
            index=-1;
        }
        return spells;
    }
};
```

