class Solution {
public:
    int BS(int t, vector<int>& p, long long success){
        long long l = 0, r = p.size()-1, mid = -1;
        while(l<=r){
            mid = (l+r)/2;
            if( (long long)t*p[mid] < success )
                l = mid+1;
            else
                r = mid-1;
        }
        return l;
    }
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        int n = spells.size(), p = potions.size(), temp;
        vector<int> ans(n, 0);
        sort(potions.begin(), potions.end());
        for(int i=0; i<n; ++i){
            temp = BS( spells[i] , potions, success );
            ans[i] = (temp == p) ? 0 : p-temp;
        }
        return ans;
    }
};
