class Solution {
public:
    // simple binary search
    int bs(vector<int> &num,int &x,long long &success){
        int l=0,r=num.size()-1;
        while(l<=r){
            int mid = l + (r-l)/2;
            long long m = (long long)x*num[mid];
            if(m>=success){
                r=mid-1;
            }else{
                l=mid+1;
            }
        }
        return l;
    }

    vector<int> successfulPairs(vector<int>& s, vector<int>& p, long long success) {
        // here we will not use any extra space just update answer to spell array
        // sort poision array
        sort(p.begin(),p.end());
        // iterate over spell array
        for(int i=0;i<s.size();i++){
            // pass poision vector , spell[i] and success to binary search
            // subtract it from poision size 
            // because we will get number of small multiplication
            s[i] = p.size()-bs(p,s[i],success);
        }
        return s;
    }
};
```

