class Solution {
    public int[] successfulPairs(int[] spells, int[] potions, long success) {

        //Sort the array so that we can apply binary search on the array
        Arrays.sort(potions);
        int pLen = potions.length, startIndex = -1;
        int[] ans = new int[spells.length];

        //Iterate over spells and find starting potion index for each spell which will give success
        for(int s = 0; s < spells.length; s++){

            //using binary search find the starting index which results into success 
            startIndex = getStartSuccessIndex(potions, spells[s], success);

            //If a valid index is found then all the indices to it's right will also result in success
            if(startIndex >= 0){
                ans[s] = pLen - startIndex;
            }else{
                //If no valid index is found then 0 results will be formed
                ans[s] = 0;
            }
        }
        return ans;
    }

    private int getStartSuccessIndex(int[] potions, long spell, long success){
        int ans = -1, p = potions.length;
        int left = 0, right = p-1, mid = (right+left)/2;
        while(left <= right){
            mid = (left+right)/2;
            //If product of mid potion value with spll is >= success then this mighte be our ans so store it into ans variable and look for smaller index on the left side. 
            if(potions[mid] * spell >= success){
                ans = mid;
                right = mid -1;
            }else {
                left = mid + 1;
            }
        }
        return ans;
    }
}
