```
typedef long long int ll ;
struct data {
    int index, count, a ;
};
bool comp(struct data A, struct data B){
    return A.a < B.a;
}
class Solution {
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        int n = spells.size(), m = potions.size();
        vector<struct data>v(n);
        for(int i = 0 ; i< n; i++){
            v[i].a = spells[i];
            v[i].index = i;
            v[i].count = 0 ;
        }
        sort(v.begin(), v.end(), comp);
        sort(potions.begin(), potions.end());
        for(int i = 0 ; i < n; i++){
            ll reqd = success/v[i].a ;
            if(success%v[i].a != 0)
                reqd ++ ;
            int ind = lower_bound(potions.begin(), potions.end(), reqd) - potions.begin();
            if(ind == m)
                v[i].count = 0 ;
            else
                v[i].count = m-ind;
        }
        vector<int>ans(n) ;
        for(auto it: v)
            ans[it.index] = it.count;

        return ans;

    }
};
```

