class Solution {
public:
	long long sellingWood(int m, int n, vector<vector<int>>& prices) {
		// try cutting horizonally  and again vertically , take the maximum...
		// after half length of cut .. values are repeating..  
		// n^3
		// WxH   cut from i of width   WxH, ixH, (W-i)xH  take maximum of these three... 
		// base cases are the prices given...

		// mxn wood  hxw 

		vector<vector<long long>> dp(m+1, vector<long long> (n+1, 0));

		for(vector<int> p: prices)
			dp[p[0]][p[1]] = p[2];

		for(int h= 1; h<= m ; h++){
			for(int w = 1; w<=n; w++){
				// try every horizontal cut for wood hxw
				for(int i = 1; i<= h/2; i++)
					dp[h][w] = max(dp[h][w], dp[i][w]+dp[h-i][w]);

				// now try every vertical cut for wood hxw
				for(int j= 1; j<= w/2; j++)
					dp[h][w] = max(dp[h][w], dp[h][j]+ dp[h][w-j]);
			}
		}

		return dp[m][n];    
	}
};

