#define ll long long int
class Solution {
public:
    vector<vector<ll>>mp;
    ll dp[205][205];
    ll rec(int m,int n)
    {
        if(m==1&&n==1)
        {
            return mp[m][n];
        }
        if(dp[m][n]!=-1)
            return dp[m][n];
        ll ans=0;
        for(int i=1;i<m;i++)
        {
            int sum=0;

            ans=max(ans,max(mp[i][n],rec(i,n))+max(mp[m-i][n],rec(m-i,n)));
        }
        for(int i=1;i<n;i++)
        {
            int sum=0;
            ans=max(ans,max(mp[m][i],rec(m,i))+max(mp[m][n-i],rec(m,n-i)));
        }

        return dp[m][n]=ans;
    }
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {
        memset(dp,-1,sizeof(dp));
        mp.clear();
        for(int i=0;i<=m+2;i++)
        {
            vector<ll>temp;
            for(int j=0;j<=n+2;j++)
                temp.push_back(0);
            mp.push_back(temp);
        }
        for(auto x : prices)
            mp[x[0]][x[1]]=x[2];

        ll ans=max(rec(m,n),mp[m][n]);
        return ans;
    }
};
