class Solution {
public:
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {


        int row = m;
        int col = n;

        int price_size = prices.size();


        long long cost[row][col];

        for(int i = 0; i < row; i++) {
            for(int j = 0; j < col; j++) {
                cost[i][j] = 0;
            }
        }

        for(int i = 0; i < price_size; i++) {

            int h = prices[i][0];
            int w = prices[i][1];
            int price = prices[i][2];


            cost[h - 1][w - 1] = price;

        }

        long long dp[row][col];

        for(int i = 0; i < row; i++) {
            for(int j = 0; j < col; j++) {
                dp[i][j] = 0;
            }
        }


        for(int i = 0; i < row; i++) {
            for(int j = 0; j < col; j++) {

                int left_col = 0;
                int right_col = j - 1;

                while(left_col <= right_col) {
                    dp[i][j] = max(dp[i][j], dp[i][left_col] + dp[i][right_col]);
                    left_col++;
                    right_col--;
                }

                int top_row = 0;
                int bottom_row = i - 1;


                while(top_row <= bottom_row) {
                    dp[i][j] = max(dp[i][j], dp[top_row][j] + dp[bottom_row][j]);
                    top_row++;
                    bottom_row--;
                }


                dp[i][j] = max(dp[i][j], cost[i][j]);
            }
        }



        return dp[row - 1][col - 1];




    }
};
