class Solution:
    def sellingWood(self, m, n, prices):
        dict1 = collections.defaultdict(int)

        for i,j,k in prices:
            dict1[(i,j)] = k

        @lru_cache(None)
        def dfs(h,w):
            if h == 0 or w == 0: return 0

            max_val = dict1[(h,w)]

            for i in range(1,h//2+1):
                max_val = max(max_val,dfs(i,w) + dfs(h-i,w))

            for j in range(1,w//2+1):
                max_val = max(max_val,dfs(h,j) + dfs(h,w-j))

            return max_val 

        return dfs(m,n)





