        //creating a pricelist array to access in O(1) time
        for(auto &it:prices){
            priceList[it[0]][it[1]]=it[2];
        }
        memset(dp,-1,sizeof(dp));
        // return max profit when having plank of size m*n
        return solve(m,n); 
