        //base case
        if(height==0 || width==0)
            return 0;

        //check the cache
        if(dp[height][width]!=-1)
            return dp[height][width];

        ll maxProfit=0;

        // when sell entire plank
        maxProfit = priceList[height][width];

        //when cut height wise
        for(int i=1;i<height;i++){
            ll curr = solve(i,width) + solve(height-i,width);
            maxProfit=max(maxProfit,curr);
        }

        //when cut width wise
        for(int i=1;i<width;i++){
            ll curr = solve(height,i) + solve(height,width-i);
            maxProfit=max(maxProfit,curr);
        }

        return dp[height][width] = maxProfit;
    }
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {

        //creating a pricelist array to access in O(1) time
        for(auto &it:prices){
            priceList[it[0]][it[1]]=it[2];
        }
        memset(dp,-1,sizeof(dp));
        // return max profit when having plank of size m*n
        return solve(m,n); 
    }
};
