var sellingWood = function(m, n, prices) {
    let map={},cache={};
    for(let i=0;i<prices.length;i++){
        let r=prices[i][0];
        let c=prices[i][1];
        map[r+"_"+c]=prices[i][2];
    }
    let ans = backtrack(m,n);
    return ans;
    function backtrack(rows,cols){
        let key = rows+"_"+cols;
        if(cache[key]!==undefined){
            return cache[key];
        }

        let p0=0,p1=0,p2=0;
        if(map[key]!==undefined){//Check if there is a price for the entire wood board
            p0 = map[key];
        }
        if(rows>1){//Check by cutting rows
            for(let i=1;i<=Math.floor(rows/2);i++){//Note: for rows=5 we just need to check 1,4 and 2,3 only as rest of 3,2 and 4,1 will give the same results. So we are runing from i=1 till i<=Math.floor(rows/2)
                let r = backtrack(rows-i,cols)+backtrack(i,cols);
                p1 = Math.max(r,p1);
            }
        }
        if(cols>1){//Check by cutting cols
            for(let i=1;i<=Math.floor(cols/2);i++){
                let r = backtrack(rows,cols-i)+backtrack(rows,i);
                p2 = Math.max(r,p2);
            }
        }
        let res = Math.max(p0,p1,p2);
        cache[key] = res;
        return res;
    }
};
