long long sellingWood(int m, int n, vector<vector<int>>& prices) {

	unordered_map<int, unordered_map<int,ll>> mp;
	for(auto& p : prices) mp[p[0]][p[1]] = p[2];

	vector<vector<ll>> dp(m+1, vector<ll>(n+1));

	for(int h=1;h <= m; h++){
		for(int w = 1; w <= n; w++){

			ll maxProfit = (mp[h].count(w) ? mp[h][w] : 0);

			// Horizontal Cuts
			for(int i=1;i<h;i++){

				ll maxProfitUp = dp[i][w];
				ll maxProfitDown = dp[h-i][w];

				ll totProfit = maxProfitUp + maxProfitDown;

				maxProfit = max(maxProfit, totProfit);
			}


			//Vertical Cuts
			for(int j=1;j<w;j++){

				ll maxProfitLeft = dp[h][j];
				ll maxProfitRight = dp[h][w-j];

				ll totProfit = maxProfitLeft + maxProfitRight;

				maxProfit = max(maxProfit, totProfit);
			}

			dp[h][w] = maxProfit;
		}
	}


	return dp[m][n];
}
