				ll maxProfitDown = dp[h-i][w];

				ll totProfit = maxProfitUp + maxProfitDown;

				maxProfit = max(maxProfit, totProfit);
			}


			//Vertical Cuts
			for(int j=1;j<w;j++){

				ll maxProfitLeft = dp[h][j];
				ll maxProfitRight = dp[h][w-j];

				ll totProfit = maxProfitLeft + maxProfitRight;

				maxProfit = max(maxProfit, totProfit);
			}

			dp[h][w] = maxProfit;
		}
	}


	return dp[m][n];
}
